import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './VehicleProfile.css';

const VehicleProfile = () => {
  const [vehicles, setVehicles] = useState([]); // State to store vehicles fetched from backend
  const [startPoint, setStartPoint] = useState('');
  const [destination, setDestination] = useState('');
  const [mapUrl, setMapUrl] = useState('');

  const [showModal, setShowModal] = useState(false);
  const [busModel, setBusModel] = useState('');
  const [driverName, setDriverName] = useState('');
  const [numberPlate, setNumberPlate] = useState('');

  // Fetch vehicles on component mount
  useEffect(() => {
    axios.get('http://localhost:5000/api/vehicles')
      .then(response => setVehicles(response.data))
      .catch(error => console.error('Error fetching vehicles:', error));
  }, []);

  const handleSaveRoute = () => {
    if (startPoint && destination) {
      const url = `https://www.google.com/maps/embed/v1/directions?key=YOUR_GOOGLE_MAPS_API_KEY&origin=${encodeURIComponent(startPoint)}&destination=${encodeURIComponent(destination)}&mode=driving`;
      setMapUrl(url);
    } else {
      alert('Please enter both starting point and destination.');
    }
  };

  const handleAddNewBus = () => {
    if (busModel && driverName && numberPlate) {
      const newBus = {
        busModel,
        driverName,
        numberPlate,
      };

      // Send the new bus data to backend
      axios.post('http://localhost:5000/api/vehicles', newBus)
        .then(response => {
          setVehicles([...vehicles, response.data]); // Add new bus to state
          setShowModal(false);
          setBusModel('');
          setDriverName('');
          setNumberPlate('');
        })
        .catch(error => console.error('Error adding new bus:', error));
    } else {
      alert('Please fill in all the bus details.');
    }
  };

  return (
    <div className="vehicleProfileContainer">
      <div className="mainContent">
        <div className="headerContainer">
          <h1 className="pageTitle">Transportation</h1>
          <Link to="/">
            <button className="backBtn">‚Üê Back</button>
          </Link>
        </div>

        <div className="profileSection">
          <div className="vehicleInfo">
            {vehicles.map(vehicle => (
              <div key={vehicle._id} className="vehicleCard">
                <img
                  className="vehicleImg"
                  src="https://jcbl.com/jcbl-images/products/school-bus/school%20banner.jpg"
                  alt="Bus"
                />
                <div className="text">
                  <p><strong>Bus No:</strong> {vehicle._id}</p>
                  <p><strong>Model:</strong> {vehicle.busModel}</p>
                  <p><strong>No.Plate:</strong> {vehicle.numberPlate}</p>
                  <p><strong>Current Driver:</strong> {vehicle.driverName}</p>
                  <p><strong>Vehicle Status:</strong> {vehicle.vehicleStatus}</p>
                  <p><strong>Last Maintenance:</strong> {new Date(vehicle.lastMaintenance).toDateString()}</p>
                </div>
              </div>
            ))}

            {/* Button to open modal for adding a new bus */}
            <button className="addBusBtn" onClick={() => setShowModal(true)}>
              Add New Bus
            </button>
          </div>

          <div className="routePlanning">
            <h3>Route Planning</h3>
            {mapUrl ? (
              <iframe
                title="Google Maps Directions"
                src={mapUrl}
                width="100%"
                height="400"
                style={{ border: '0' }}
                allowFullScreen=""
                loading="lazy"
              />
            ) : (
              <p>No route selected</p>
            )}
            <div className="routeInputs">
              <input
                type="text"
                placeholder="Enter starting point"
                value={startPoint}
                onChange={(e) => setStartPoint(e.target.value)}
              />
              <input
                type="text"
                placeholder="Enter destination"
                value={destination}
                onChange={(e) => setDestination(e.target.value)}
              />
              <button className="saveRouteBtn" onClick={handleSaveRoute}>
                Set Route
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Modal for adding new bus */}
      {showModal && (
        <div className="modal">
          <div className="modalContent">
            <h3>Add New Bus</h3>
            <input
              type="text"
              placeholder="Bus Model"
              value={busModel}
              onChange={(e) => setBusModel(e.target.value)}
            />
            <input
              type="text"
              placeholder="Driver Name"
              value={driverName}
              onChange={(e) => setDriverName(e.target.value)}
            />
            <input
              type="text"
              placeholder="Number Plate"
              value={numberPlate}
              onChange={(e) => setNumberPlate(e.target.value)}
            />
            <button className="saveBusBtn" onClick={handleAddNewBus}>
              Save Bus
            </button>
            <button className="cancelBtn" onClick={() => setShowModal(false)}>
              Cancel
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default VehicleProfile;
